const items = $input.all();
const splitItems = items.flatMap((item) => {
  const output = item?.json?.output;
  return output.split(",").map((value) => ({ json: { output: value } }));
});
return splitItems;

// Initialize an empty array to store the final flattened results
const allOrganicResults = [];

// Loop through each item output by the HTTP Request node
// Each 'item' represents the result of one API call
for (const item of $input.all()) {

  // Check if the 'organic_results' key exists in the current item's JSON,
  // is an array, and is not empty.
  if (item.json.organic_results && Array.isArray(item.json.organic_results) && item.json.organic_results.length > 0) {

    // Loop through each organic result within the current item
    for (const organicResult of item.json.organic_results) {

      // Create a new object containing only the desired fields
      // Use || null as a safety check in case a field is missing
      const extractedResult = {
        title: organicResult.title || null,
        link: organicResult.link || null, // Use the 'link' field
        snippet: organicResult.snippet || null
      };

      // Add this new object to our final results array
      allOrganicResults.push(extractedResult);
    }
  }
  // If organic_results doesn't exist or is empty for an item, we just skip it.
}

// Return the final flattened array.
// n8n expects an array of items, where each item has a 'json' property.
// To output our flat list as the primary data, we wrap it like this:
return allOrganicResults.map(result => ({ json: result }));

// If you have only one input item and want to return the array directly
// (less common but sometimes works depending on workflow):
// return allOrganicResults;
// However, the [{ json: ... }] format is the standard and safer way.

