// Get the raw input string from the previous node
const firstInput = $input.first().json;
if (!firstInput.hasOwnProperty('output')) {
  return [{ json: { error: "Missing 'output' property in input data." } }];
}
const rawText = firstInput.output;

// Extract the JSON part from the string
const jsonString = rawText.replace(/```json\n|\n```/g, "").trim();

try {
    // Parse the extracted string into a JSON object
    const inputData = JSON.parse(jsonString);

    // Create the transformed output structure
    const transformedData = {
        "content": {
            "assessment": {},
            "Specific Criticisms": {},
            "Overall Quality Assessment": inputData["Overall Quality Assessment"] || "",
            "Proceed to Next Step": inputData["Proceed to next step"] || "No"
        },
        "refusal": null,
        "annotations": [],
        "logprobs": null,
        "finish_reason": "stop"
    };

    // Convert assessment scores to "x/10" format dynamically
    if (inputData.assessment) {
        for (const key in inputData.assessment) {
            transformedData.content.assessment[key] = `${inputData.assessment[key]}/10`;
        }
    }

    // Process Specific Criticisms dynamically
    if (inputData["Specific Criticisms"]) {
        for (const key in inputData["Specific Criticisms"]) {
            const criticism = inputData["Specific Criticisms"][key];
            transformedData.content["Specific Criticisms"][key] = criticism !== "N/A" ? criticism : "";
        }
    }

    // Return the transformed JSON
    return [{ json: transformedData }];

} catch (error) {
    return [{ json: { error: "Failed to parse JSON", details: error.message } }];
}

const results = $input.all().map(item => {
  const data = item.json;
  return `title: ${data.title}, link: ${data.link}, snippet: ${data.snippet}`;
});

return [
  {
    json: {
      resultsArray: results
    }
  }
];

