// Get the input data
const input = $input.first().json.output;

// Extract categories and tags using RegEx
const categoryMatch = input.match(/Categories:\s*\[([^\]]*)\]/);
const tagMatch = input.match(/Tags:\s*\[([^\]]*)\]/);

// Convert string to array of numbers
const categories = categoryMatch ? categoryMatch[1].split(',').map(item => Number(item.trim())) : [];
const tags = tagMatch ? tagMatch[1].split(',').map(item => Number(item.trim())) : [];

// Return as n8n JSON output
return [
  {
    json: {
      categories,
      tags
    }
  }
];

return [
